import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './ProductDetails.css'; // Ensure the CSS file is in the same directory

const products = [
  { id: 1, name: 'Smartphone X', image: '/images/smartphone.jpg', price: '$499', description: 'Latest model with advanced features.' },
  { id: 2, name: 'Laptop Y', image: '/images/laptop.jpg', price: '$899', description: 'High-performance laptop for work and gaming.' },
  { id: 3, name: 'Smartwatch Z', image: '/images/smartwatch.jpg', price: '$199', description: 'Keep track of your health and notifications.' },
];

function ProductDetails() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Fetch the product details by ID (simulated here)
    const selectedProduct = products.find(p => p.id === parseInt(id));
    setProduct(selectedProduct);
  }, [id]);

  if (!product) return <p>Loading...</p>;

  return (
    <div className="product-details-container">
      <div className="product-image">
        <img src={product.image} alt={product.name} />
      </div>
      <div className="product-info">
        <h1>{product.name}</h1>
        <p className="description">{product.description}</p>
        <span className="price">{product.price}</span>
        <div className="product-actions">
          <button>Add to Cart</button>
        </div>
      </div>
    </div>
  );
}

export default ProductDetails;
